CWup.3 <- list("Param. 3, given Up" = readRDS(here("MCS/MCS Tables/CW+Up Mean-SD 3.rds")), "100%" = c(100) )
CWup.4 <- list("Param. 4, given Up" = readRDS(here("MCS/MCS Tables/CW+Up Mean-SD 4.rds")), "100%" = c(100) )

